import axios from 'axios';
import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import "./login.css";

export default function Register() {
    const [formErrors, setFormErrors] = useState({});
    const [credentials, setCredentials] = useState({
        username:undefined,
        email: undefined,
        phone: undefined,
        password: undefined,
})
const {loading, error, dispatch} = useContext(AuthContext)

const navigate = useNavigate()
const handleChange = (e) => {
    setCredentials(prev=>({...prev, [e.target.id]: e.target.value}))
}

const validateForm = (values) => {
    const errorf = {};
    const regex = /^[^\s+@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.username) {
      formErrors = "First Name is required";
    }
    if (!values.lname) {
      errorf.lname = "Phone is required";
    }
    if (!values.email) {
      errorf.email = "Email is required";
    } else if (!regex.test(values.email)) {
      error.email = "This is not a valid email format!";
    }
    if (!values.password) {
      errorf.password = "Password is required";
    } else if (values.password.length < 6) {
      errorf.password = "Password must be more than 6 characters";
    } else if (values.password.length > 10) {
      errorf.password = "Password cannot exceed more than 10 characters";
    }
    if (!values.cpassword) {
      error.cpassword = "Confirm Password is required";
    } else if (values.cpassword !== values.password) {
      errorf.cpassword = "Confirm password and password should be same";
    }
    return errorf;
  };

const handleClick = async(e) =>{
    e.preventDefault()
    dispatch({type:"LOGIN_START"})
    try{
        const res = await axios.post("/auth/register", credentials)
        dispatch({type: "LOGIN_SUCCESS", payload: res.data})
        navigate("/")
    }catch(err){
        dispatch({type:"LOGIN_FAILURE", payload: err.response.data})
    }
}

  return (
    
    <div className="login">
        <div style={{height: 200}}>
            
        </div>
        <div className='lContainer'>
        <span className='loginText'>Register Here</span><br></br>
            <form>
            <input type="text" placeholder='Username' id='username' onChange={handleChange} className='lInput'/><br></br><br></br>
            <input type="email" placeholder='Email' id='email' onChange={handleChange} className='lInput'/><br></br><br></br>
            <input type="text" placeholder='Phone' id='phone' onChange={handleChange} className='lInput'/><br></br><br></br>
            <input type="password" placeholder='Password' id='password' onChange={handleChange} className='lInput'/><br></br><br></br>
            <button className="lButton" disabled={loading} onClick={handleClick}>Register</button><br></br>
            {error && <span className='logspan'>{error.message}</span>}
            {/* <p>{formErrors}</p> */}
            </form>
        </div>
        <NavLink to="/login">Already registered? Login</NavLink>
      
    </div>

 
  )
}
