import axios from 'axios';
import React, { useContext, useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { AuthContext } from '../../context/AuthContext';
import "./login.css";

export default function Login() {
    const [credentials, setCredentials] = useState({
        username:undefined,
        password: undefined,
})
const {loading, error, dispatch} = useContext(AuthContext)

const navigate = useNavigate()
const [toastShown, setToastShown] = useState(false);
const handleChange = (e) => {
    setCredentials(prev=>({...prev, [e.target.id]: e.target.value}))
}
const passwordValidate = (e) => {
    const inputPassword = e.target.value;
    setCredentials.password(inputPassword);
 
    if (inputPassword.length < 7) {
      if (!toastShown) {
        toast.error("Password must be more than 6 characters.");
        setToastShown(true); // Set the flag so the toast won't show again
      }
    } else {
      setToastShown(false); // Reset the flag when the error is corrected
    }
  };
const handleClick = async(e) =>{
    e.preventDefault()
    dispatch({type:"LOGIN_START"})
    try{
        const res = await axios.post("/auth/login", credentials)
        dispatch({type: "LOGIN_SUCCESS", payload: res.data})
        navigate("/")
    }catch(err){
        dispatch({type:"LOGIN_FAILURE", payload: err.response.data})
        toast.error(error.message);
    }
}

  return (
    
    <div className="login">
        <div style={{height: 200}}>
            
        </div>
        <div className='lContainer'>
        <div style={"color: blue;"}><span className='loginText'>Login Here</span><br></br></div>
            <input type="text" placeholder='Username' id='username' onChange={handleChange} className='lInput'/><br></br>
            <input type="password" placeholder='Password' id='password' onChange={passwordValidate} className='lInput'/><br></br>
            <button className="lButton" disabled={loading} onClick={handleClick}>Login</button><br></br>
            {error && <span className='logspan'>{error.message}</span>}
            <NavLink to="/register">Don't have an Account? Register</NavLink><br></br><br></br>
        </div>
      
    </div>

 
  )
}
